contracts/HedgeExecutor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

// GMX V2 Interfaces
interface IGMXExchangeRouter {
    function createOrder(
        address account,
        address[] calldata addressItems,
        uint256[] calldata uintItems,
        bytes32[] calldata bytesDataItems,
        bytes calldata data
    ) external payable returns (bytes32);
}

interface IGMXRouter {
    function approvePlugin(address plugin) external;
    function plugin() external view returns (address);
}

interface IGMXOrderHandler {
    function executeOrder(bytes32 key, address keeper) external;
}

/**
 * @title HedgeExecutor
 * @notice Executes hedge orders on GMX (Arbitrum).
 *         Receives cross-chain messages from StrategyManager (Polygon) via LayerZero.
 *         Places perpetual orders directly on GMX on-chain.
 */
contract HedgeExecutor is Ownable, ReentrancyGuard {
    struct HedgeOrder {
        uint256 strategyId;
        address user;
        string asset; // e.g., "BTC", "ETH"
        bool isLong;
        uint256 amount; // USDC amount
        uint256 maxSlippageBps;
        bool executed;
        bytes32 gmxOrderKey;
    }

    // strategyId => HedgeOrder
    mapping(uint256 => HedgeOrder) public hedgeOrders;
    mapping(uint256 => bool) public ordersClosed;
    mapping(bytes32 => uint256) public gmxKeyToStrategyId; // GMX order key => strategy ID

    event HedgeOrderCreated(
        uint256 indexed strategyId,
        address indexed user,
        string asset,
        bool isLong,
        uint256 amount,
        bytes32 gmxOrderKey
    );
    event HedgeOrderClosed(uint256 indexed strategyId, uint256 realizedPnL);
    event MessageReceived(uint256 indexed strategyId, address indexed user);
    event HedgeOrderExecuted(uint256 indexed strategyId, bytes32 gmxOrderKey);

    address public strategyManager;

    // GMX contracts on Arbitrum
    IGMXExchangeRouter public gmxExchangeRouter;
    IGMXRouter public gmxRouter;
    IERC20 public usdc;

    // Market addresses for GMX (index token to market address mapping)
    mapping(string => address) public assetMarkets;

    constructor(address _gmxExchangeRouter, address _gmxRouter, address _usdc) Ownable(msg.sender) {
        require(_gmxExchangeRouter != address(0), "invalid GMX router");
        require(_gmxRouter != address(0), "invalid GMX router");
        require(_usdc != address(0), "invalid USDC");

        gmxExchangeRouter = IGMXExchangeRouter(_gmxExchangeRouter);
        gmxRouter = IGMXRouter(_gmxRouter);
        usdc = IERC20(_usdc);
    }

    /**
     * @notice Set StrategyManager address (can only be called once by owner)
     */
    function setStrategyManager(address _strategyManager) external onlyOwner {
        require(_strategyManager != address(0), "invalid address");
        require(strategyManager == address(0), "already set");
        strategyManager = _strategyManager;
    }

    /**
     * @notice Configure market addresses for assets
     *         Maps asset symbols (e.g., "BTC", "ETH") to GMX market addresses
     */
    function setAssetMarket(string calldata asset, address marketAddress) external onlyOwner {
        require(marketAddress != address(0), "invalid market");
        assetMarkets[asset] = marketAddress;
    }

    /**
     * @notice Receive hedge order from StrategyManager and execute it on GMX
     *         Can only be called by StrategyManager
     */
    function createHedgeOrder(
        uint256 strategyId,
        address user,
        string calldata asset,
        bool isLong,
        uint256 amount,
        uint256 maxSlippageBps
    ) external nonReentrant {
        require(msg.sender == strategyManager, "only StrategyManager");
        require(amount > 0, "amount=0");
        require(assetMarkets[asset] != address(0), "asset not supported");

        // Check USDC balance and allowance
        require(usdc.balanceOf(address(this)) >= amount, "insufficient USDC balance");

        // Create the GMX order
        bytes32 gmxOrderKey = _executeGMXOrder(asset, isLong, amount, maxSlippageBps);

        // Store hedge order details
        hedgeOrders[strategyId] = HedgeOrder({
            strategyId: strategyId,
            user: user,
            asset: asset,
            isLong: isLong,
            amount: amount,
            maxSlippageBps: maxSlippageBps,
            executed: true,
            gmxOrderKey: gmxOrderKey
        });

        // Map GMX order key back to strategy for settlement
        gmxKeyToStrategyId[gmxOrderKey] = strategyId;

        emit HedgeOrderCreated(strategyId, user, asset, isLong, amount, gmxOrderKey);
        emit MessageReceived(strategyId, user);
        emit HedgeOrderExecuted(strategyId, gmxOrderKey);
    }

    /**
     * @notice Internal function to execute a GMX perpetual order
     *         Constructs the necessary parameters and calls GMXExchangeRouter
     */
    function _executeGMXOrder(
        string calldata asset,
        bool isLong,
        uint256 amount,
        uint256 maxSlippageBps
    ) internal returns (bytes32 orderKey) {
        address marketAddress = assetMarkets[asset];

        // Approve GMX to spend USDC
        usdc.approve(address(gmxRouter), amount);

        // Build order parameters for GMX V2
        // This is a simplified version - adjust based on actual GMX V2 order structure
        address[] memory addressItems = new address[](4);
        addressItems[0] = marketAddress;        // market
        addressItems[1] = msg.sender;           // receiver (StrategyManager)
        addressItems[2] = address(usdc);        // initialCollateralToken
        addressItems[3] = address(0);           // swapPath (none for direct orders)

        uint256[] memory uintItems = new uint256[](6);
        uintItems[0] = amount;                  // sizeDeltaUsd (in USDC value)
        uintItems[1] = amount;                  // initialCollateralDeltaAmount
        uintItems[2] = 0;                       // triggerPrice
        uintItems[3] = maxSlippageBps;          // acceptablePrice / slippage
        uintItems[4] = 0;                       // executionFee (handled separately)
        uintItems[5] = isLong ? 1 : 0;          // orderType (1 = MarketIncrease for longs)

        bytes32[] memory bytesDataItems = new bytes32[](0);

        // Create order via GMX ExchangeRouter
        orderKey = gmxExchangeRouter.createOrder(
            address(this),
            addressItems,
            uintItems,
            bytesDataItems,
            ""
        );

        return orderKey;
    }

    /**
     * @notice Close hedge order at maturity
     *         Called by backend/bridge when strategy matures.
     *         Submits a close/reduce order to GMX
     */
    function closeHedgeOrder(uint256 strategyId, int256 realizedPnL) external nonReentrant onlyOwner {
        require(!ordersClosed[strategyId], "already closed");

        HedgeOrder storage hedge = hedgeOrders[strategyId];
        require(hedge.amount > 0, "order not found");

        // TODO: Call GMX.closePosition() via ExchangeRouter
        // For now, mark as closed
        ordersClosed[strategyId] = true;
        emit HedgeOrderClosed(strategyId, uint256(realizedPnL));
    }

    /**
     * @notice Get hedge order details
     */
    function getHedgeOrder(uint256 strategyId) external view returns (HedgeOrder memory) {
        return hedgeOrders[strategyId];
    }

    /**
     * @notice Get strategy ID from GMX order key (for settlement tracking)
     */
    function getStrategyIdFromGMXOrder(bytes32 gmxOrderKey) external view returns (uint256) {
        return gmxKeyToStrategyId[gmxOrderKey];
    }

    /**
     * @notice Check if a hedge order has been executed
     */
    function isOrderExecuted(uint256 strategyId) external view returns (bool) {
        return hedgeOrders[strategyId].executed;
    }

    /**
     * @notice Emergency withdrawal of USDC (owner only)
     */
    function withdrawUSDC(uint256 amount) external onlyOwner {
        require(usdc.transfer(msg.sender, amount), "transfer failed");
    }
}

contracts/StrategyManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

// Interface to HedgeExecutor on the same chain
interface IHedgeExecutor {
    function createHedgeOrder(
        uint256 strategyId,
        address user,
        string calldata asset,
        bool isLong,
        uint256 amount,
        uint256 maxSlippageBps
    ) external;
}

/**
 * @title StrategyManager
 * @notice Minimal contract to create, sell, and settle hedging strategies.
 *         Designed to unblock frontend and bot integrations. Execution of
 *         off-chain orders (Polymarket/DEX) is coordinated by an external
 *         bridge/backend listening to emitted events.
 */
contract StrategyManager is Ownable, ReentrancyGuard {
    struct PolymarketOrder {
        string marketId; // external id reference
        bool isYes; // YES/NO side
        uint256 amount; // quote amount denominated in USDC (6 decimals)
        uint256 maxPriceBps; // max price in basis points of 1e4 (e.g. 500 = 5.00%)
    }

    struct HedgeOrder {
        string dex; // e.g. GMX / Hyperliquid / Gains
        string asset; // e.g. BTC, ETH
        bool isLong; // long/short
        uint256 amount; // quote amount denominated in USDC (6 decimals)
        uint256 maxSlippageBps; // basis points
    }

    struct StrategyDetails {
        PolymarketOrder[] polymarketOrders;
        HedgeOrder[] hedgeOrders;
        uint256 expectedProfitBps; // expected net profit in bps
    }

    struct Strategy {
        uint256 id;
        string name;
        uint256 feeBps; // platform fee in bps
        uint256 maturityTs; // unix timestamp
        bool active;
        StrategyDetails details;
        // settlement
        bool settled;
        uint256 payoutPerUSDC; // 6 decimals, e.g. 1.08e6 means 1.08 USDC per 1 USDC net
    }

    struct UserPosition {
        uint256 strategyId;
        uint256 amount; // net principal in USDC (after fee)
        uint256 purchaseTs;
        bool claimed;
    }

    IERC20 public immutable usdc;
    uint256 public nextStrategyId = 1;

    // strategyId => Strategy
    mapping(uint256 => Strategy) public strategies;
    // user => positions
    mapping(address => UserPosition[]) public userPositions;

    // HedgeExecutor on same chain (Arbitrum)
    IHedgeExecutor public hedgeExecutor;

    // Events used by the bridge/backend to coordinate off-chain execution
    event StrategyCreated(uint256 indexed strategyId, string name, uint256 maturityTs);
    event StrategyPurchased(
        uint256 indexed strategyId,
        address indexed user,
        uint256 grossAmount,
        uint256 netAmount // Amount to bridge to Polygon for Polymarket
    );
    event OrdersExecuted(uint256 indexed strategyId, address indexed user, bool success);
    event StrategyClaimed(uint256 indexed strategyId, address indexed user, uint256 payoutAmount);
    event StrategySettled(uint256 indexed strategyId, uint256 payoutPerUSDC);

    constructor(address _usdc, address _hedgeExecutor) Ownable(msg.sender) {
        require(_usdc != address(0), "USDC address required");
        require(_hedgeExecutor != address(0), "HedgeExecutor address required");
        usdc = IERC20(_usdc);
        hedgeExecutor = IHedgeExecutor(_hedgeExecutor);
    }

    // ------------------
    // Admin/Bot functions
    // ------------------

    function createStrategy(
        string calldata name,
        uint256 feeBps,
        uint256 maturityTs,
        PolymarketOrder[] calldata pmOrders,
        HedgeOrder[] calldata hedgeOrders,
        uint256 expectedProfitBps
    ) external onlyOwner returns (uint256 strategyId) {
        require(maturityTs > block.timestamp, "maturity must be future");
        require(feeBps <= 2000, "fee too high"); // <=20%

        strategyId = nextStrategyId++;

        // Copy arrays into storage
        Strategy storage s = strategies[strategyId];
        s.id = strategyId;
        s.name = name;
        s.feeBps = feeBps;
        s.maturityTs = maturityTs;
        s.active = true;
        s.details.expectedProfitBps = expectedProfitBps;
        s.settled = false;
        s.payoutPerUSDC = 0;

        for (uint256 i = 0; i < pmOrders.length; i++) {
            s.details.polymarketOrders.push(pmOrders[i]);
        }
        for (uint256 j = 0; j < hedgeOrders.length; j++) {
            s.details.hedgeOrders.push(hedgeOrders[j]);
        }

        emit StrategyCreated(strategyId, name, maturityTs);
    }

    // ------------------
    // User functions
    // ------------------

    function buyStrategy(uint256 strategyId, uint256 grossAmount) external nonReentrant {
        Strategy storage s = strategies[strategyId];
        require(s.active, "strategy inactive");
        require(!s.settled, "strategy settled");
        require(block.timestamp < s.maturityTs, "strategy matured");
        require(grossAmount > 0, "amount=0");

        // pull funds
        require(usdc.transferFrom(msg.sender, address(this), grossAmount), "USDC transfer failed");

        // fee and net calculation
        uint256 feeAmount = (grossAmount * s.feeBps) / 10_000;
        uint256 netAmount = grossAmount - feeAmount;

        // record user position
        userPositions[msg.sender].push(
            UserPosition({ strategyId: strategyId, amount: netAmount, purchaseTs: block.timestamp, claimed: false })
        );

        emit StrategyPurchased(strategyId, msg.sender, grossAmount, netAmount);

        // Trigger hedge orders on HedgeExecutor (same chain)
        for (uint256 i = 0; i < s.details.hedgeOrders.length; i++) {
            HedgeOrder storage ho = s.details.hedgeOrders[i];
            hedgeExecutor.createHedgeOrder(strategyId, msg.sender, ho.asset, ho.isLong, ho.amount, ho.maxSlippageBps);
        }
    }

    function claimStrategy(uint256 strategyId) external nonReentrant {
        Strategy storage s = strategies[strategyId];
        require(block.timestamp >= s.maturityTs, "not matured");
        require(s.settled, "not settled");
        require(s.payoutPerUSDC > 0, "payout unset");

        // find unclaimed position (simple linear scan for MVP)
        UserPosition[] storage positions = userPositions[msg.sender];
        uint256 idx = type(uint256).max;
        for (uint256 i = 0; i < positions.length; i++) {
            if (positions[i].strategyId == strategyId && !positions[i].claimed) {
                idx = i;
                break;
            }
        }
        require(idx != type(uint256).max, "no position");

        UserPosition storage p = positions[idx];
        p.claimed = true;

        // Payout is proportional to contributed net amount times settlement factor
        uint256 payout = (p.amount * s.payoutPerUSDC) / 1_000_000; // 6 decimals
        require(usdc.transfer(msg.sender, payout), "USDC payout failed");

        emit StrategyClaimed(strategyId, msg.sender, payout);
    }

    // ------------------
    // Bridge/Executor hooks (owner for MVP)
    // ------------------
    function reportExecutionStatus(uint256 strategyId, address user, bool success) external onlyOwner {
        emit OrdersExecuted(strategyId, user, success);
    }

    /// @notice Settle a strategy with a global payout-per-USDC factor after all legs are closed off-chain
    function settleStrategy(uint256 strategyId, uint256 payoutPerUSDC) external onlyOwner {
        Strategy storage s = strategies[strategyId];
        require(block.timestamp >= s.maturityTs, "not matured");
        require(!s.settled, "already settled");
        require(payoutPerUSDC > 0, "invalid payout");
        s.settled = true;
        s.payoutPerUSDC = payoutPerUSDC; // 6 decimals
        // deactivate further buys just in case
        s.active = false;
        emit StrategySettled(strategyId, payoutPerUSDC);
    }
}
